@Composable
fun BlogScreen(
    blogViewModel: BlogViewModel = hiltViewModel()
){
    val blog = blogViewModel.blog.collectAsState()

    Scaffold (
        topBar = { GeneralTopBar() },
        bottomBar = { GeneralBottomBar() },
        containerColor = colorResource(id = R.color.white)
    ){paddingValues ->
        BlogSuccessDisplay(
            blog = blog.value,
            modifier = Modifier
                .padding(paddingValues)
                .fillMaxSize()
        )
    }
}

@Composable
fun BlogSuccessDisplay(blog: BlogDC, modifier: Modifier) {
    val title: Spanned = Html.fromHtml(blog.title, Html.FROM_HTML_MODE_LEGACY)

    Column(
        modifier = modifier
            .fillMaxSize()
            .verticalScroll(rememberScrollState())
            .background(colorResource(id = R.color.white))
    ) {
        Text(
            text = title.toString(),
            fontSize = 22.sp,
            modifier = Modifier.padding(10.dp, 5.dp, 10.dp, 3.dp),
            fontWeight = FontWeight.SemiBold,
            color = colorResource(id = R.color.black)
        )
        Text(
            text = formatTime(blog.date),
            fontSize = 16.sp,
            modifier = Modifier.padding(10.dp, 0.dp),
            fontWeight = FontWeight.Normal,
            color = colorResource(id = R.color.text_gray)
        )
        Text(
            text = formatDate(blog.date),
            fontSize = 16.sp,
            modifier = Modifier.padding(10.dp, 0.dp),
            fontWeight = FontWeight.Normal,
            color = colorResource(id = R.color.text_gray)
        )
        Spacer(modifier = Modifier.height(10.dp))

        BlogWebView(
            content = blog.content,
            modifier = Modifier
                .fillMaxWidth()
                .heightIn(min = 100.dp) // Set a minimum height for proper rendering
                .padding(horizontal = 10.dp)
        )
    }
}


